import { Box, Button, Text, Input } from '@chakra-ui/react';

const Posts = () => {
  const handleUpload = async e => {
    console.log(e)
  };

  return (
    <Box mt={6}>
      <Input id="imageInput" type="file" hidden onChange={handleUpload} />
      <Button
        as="label"
        htmlFor="imageInput"
        colorScheme="blue"
        variant="outline"
        mb={4}
        cursor="pointer"
      >
        Upload
      </Button>
      <Text textAlign="left" mb={4}>
        Posts
      </Text>
    </Box>
  );
};
export default Posts;
